pub enum ColorMatrix<'a> {
    Matrix(&'a [f64; ]),
    Saturate(NormalizedValue),
    HueRotate(f64),
    LuminanceToAlpha,
}

pub enum TransferFunction<'a> {

    /// Keeps component as is.
    Identity,

    /// Applies a linear interpolation to a component.
    ///
    /// The number list can be empty.
    Table(&'a [f64]),

    /// Applies a step function to a component.
    ///
    /// The number list can be empty.
    Discrete(&'a [f64]),

    /// Applies a linear shift to a component.
    Linear {
        slope: f64,
        intercept: f64,
    },

    /// Applies an exponential shift to a component.
    Gamma {
        amplitude: f64,
        exponent: f64,
        offset: f64,
    },
}

pub enum LightSource {
    DistantLight {
        azimuth: f64,
        elevation: f64,
    },
    PointLight {
        x: f64,
        y: f64,
        z: f64,
    },
    SpotLight {
        x: f64,
        y: f64,
        z: f64,
        points_at_x: f64,
        points_at_y: f64,
        points_at_z: f64,
        specular_exponent: f64,
        limiting_cone_angle: Option<f64>,
    },
}
